(function(){
if(window.UNB_LIB_LOADED)return;
window.UNB_LIB_LOADED=true;

var dataReceived=false,slowTimer,currentRSS="";

window.retryFetch=function(){
 var s=document.getElementById("unbFeedScript");
 if(s)s.remove();
 var n=document.getElementById("unbNetStatus");
 if(n)n.style.display="none";
 initFetch(currentRSS);
};

function shuffle(a){
 for(var i=a.length-1;i>0;i--){
  var j=Math.floor(Math.random()*(i+1)),t=a[i];
  a[i]=a[j];a[j]=t;
 }
 return a;
}

window.myLibraryLoad=function(json){
 dataReceived=true;
 clearTimeout(slowTimer);

 var items=json.feed.entry,
     grid=document.getElementById("unbLibGrid"),
     btn=document.getElementById("unbLibBtn");

 if(!grid||!items)return;

 items=shuffle(items);
 grid.innerHTML="";

 var maxItems = items.length < 6 ? items.length : 6;

 for(var i=0;i<maxItems;i++){
  var p=items[i],t=p.title.$t;
  var d=t.replace(/\s*[-â€“â€”|]?\s*(Read|Download|PDF).*/i,"")
         .replace(/[\(\[].*?[\)\]]/g,"").trim();

  var l="",img="https://via.placeholder.com/200x300?text=No+Image";
  for(var j=0;j<p.link.length;j++){
   if(p.link[j].rel=="alternate"){l=p.link[j].href;break;}
  }

  if(p.media$thumbnail){
   img=p.media$thumbnail.url.replace(/\/s[0-9]+.*?\//,"/w200-h300-c/");
  }

  grid.innerHTML+=
   '<a class="unb-rn-lib-card" href="'+l+'" title="'+t+'">'+
   '<img src="'+img+'" loading="lazy" decoding="async">'+
   '<div class="unb-rn-lib-title">'+d+'</div></a>';
 }

 if(btn)btn.style.display="block";
};

function initFetch(rss){
 if(!rss)return;
 currentRSS=rss;

 var sc=document.createElement("script");
 sc.src=rss;
 sc.id="unbFeedScript";
 document.body.appendChild(sc);

 slowTimer=setTimeout(function(){
  if(!dataReceived){
   var n=document.getElementById("unbNetStatus");
   if(n)n.style.display="block";
  }
 },8000);
}

document.addEventListener("DOMContentLoaded",function(){
 var wrap=document.querySelector(".unb-rn-lib-wrap");
 if(!wrap)return;
 var rss=wrap.getAttribute("data-rss");
 initFetch(rss);
});
})();
