document.addEventListener("DOMContentLoaded", function() {
  // Ù¾ÙˆØ³Ù¹ Ú©ÛŒ ØªÙ…Ø§Ù… ØªØµØ§ÙˆÛŒØ± Ú©Ùˆ Ø§ÛŒÚ© Ø³Ø§ØªÚ¾ Ù†Ú©Ø§Ù„ÛŒÚº
  const allImages = document.querySelectorAll('.post-body img, .entry-content img');

  allImages.forEach((img, index) => {
    if (index === 0) {
      // ØµØ±Ù Ù¾ÛÙ„ÛŒ ØªØµÙˆÛŒØ± Ú©Û’ Ù„ÛŒÛ’: High Priority
      img.setAttribute('fetchpriority', 'high');
      img.setAttribute('loading', 'eager');
      img.setAttribute('decoding', 'async');
    } else {
      // Ø¨Ø§Ù‚ÛŒ ØªÙ…Ø§Ù… ØªØµØ§ÙˆÛŒØ± Ú©Û’ Ù„ÛŒÛ’: Lazy Loading
      img.setAttribute('loading', 'lazy');
      img.setAttribute('decoding', 'async');
      // ØºÛŒØ± Ø¶Ø±ÙˆØ±ÛŒ ÛØ§Ø¦ÛŒ Ù¾Ø±Ø§Ø¦ÛŒÙˆØ±Ù¹ÛŒ ÛÙ¹Ø§Ù†Û’ Ú©Û’ Ù„ÛŒÛ’ (Ø§Ú¯Ø± Ù¾ÛÙ„Û’ Ø³Û’ Ù„Ú¯ÛŒ ÛÙˆ)
      img.removeAttribute('fetchpriority');
    }
  });

  if (allImages.length > 0) {
    console.log('Images Optimized: First image High Priority, others Lazy Loaded.');
  }
});
